@Test
public void testTop2Queries() {
  String[] args = {
      "n=2",
      };

  PigTest test = new PigTest("top_queries.pig", args);

  String[] input = {
      "yahoo",
      "yahoo",
      "yahoo",
      "twitter",
      "facebook",
      "facebook",
      "linkedin",
  };

  String[] output = {
      "(yahoo,3)",
      "(facebook,2)",
  };

  test.assertOutput("data", input, "queries_limit", output);
}

data =
    LOAD 'input'
    AS (query:CHARARRAY);

queries_group =
    GROUP data
    BY query; 

queries_count = 
    FOREACH queries_group 
    GENERATE 
        group AS query, 
        COUNT(data) AS total;

queries_ordered =
    ORDER queries_count
    BY total DESC, query;

queries_limit =
    LIMIT queries_ordered $n;

STORE queries_limit INTO 'output';

@Test
public void testTop2Queries() {
  String[] args = {
      "n=2",
      };

  PigTest test = new PigTest("top_queries.pig", args);

  String[] mockData = {
      "yahoo",
      "yahoo",
      "yahoo",
      "twitter",
      "facebook",
      "facebook",
      "linkedin",
  };

  //You should cache the map if you can
  String schema = test.getAliasToSchemaMap().get("data");
  test.mockAlias("data", mockData, schema);

  String[] output = {
      "(yahoo,3)",
      "(facebook,2)",
  };

  test.assertOutputAnyOrder("queries_limit", output);
}

test = new PigTest(PIG_SCRIPT, args);   
test.unoverride("STORE");
test.runScript();
